function activitycallback(activity){
	if(activity.template_id !== "scrwfp_tp"){
		return;
	}
	var $chart = $(this).find(".activity-chart");
	if($chart.length===0){
		return;
	}
	var pieData = $.parseJSON(activity.data.pcxx || "[]");
	var legendData = [activity.data.pcmc];
	var pieChart = echarts.init($chart.get(0));
	var pieChartOption = {
		    tooltip : {
		        trigger: 'item',
		        formatter: "{a} <br/>{b} : {c} ({d}%)",
		        textStyle:{
		            fontSize:11
		        }
		    },
		    legend: {
		        orient: 'horizontal',
		        textStyle:{
		            fontSize:11
		        },
		        formatter:"当前批次",
		        left:"center",
		        //itemWidth:10,
		        itemHeight:10,
		        data: legendData
		    },
		    series : [
		        {
		            name: '生产任务分批',
		            type: 'pie',
		            radius : '45',
		            center: ['50%', '70'],
		            hoverAnimation:false,
		             label:{
		                normal:{
		                	show:false,
		                    textStyle:{
		                        fontSize:11
		                    }
		                }
		            },
		            labelLine:{
		            	normal:{
		            		length:2,
		            		length2:2
		            	}
		            },
		            data:pieData,
		            itemStyle: {
		                emphasis: {
		                    shadowBlur: 5,
		                    shadowOffsetX: 0,
		                    shadowColor: 'rgba(0, 0, 0, 0.5)'
		                }
		            }
		        }
		    ]
		};
		pieChart.setOption(pieChartOption);
}

var mega = ui.mega("workshop");
function ready() {
	mega.setNodes(ui.bag("data").get("nodes"));
	mega.setToolBtns(ui.bag("data").get("toolBtns"));
	mega.loadData(ui.bag("data").get("data"));

	ui.activities("activities").setUrl(ui.getUrl("/activity/service/list"));
	startObtain();
}

function startObtain() {			
	obtain();
}
function stopObtain() {
	clearInterval(obtainTimer);
	obtainTimer = null;
}
var lastTime,type="",size=10,obtainTimer;
function obtain(){
	var params = {};
	if(lastTime) {
		params.after = lastTime;
	}
	params.type=type;
	params.size=size;
	ui.post(ui.getUrl("/activity/service/list"),params,function(data) {
		if(data.length) {
			ui.activities("activities").prependData(data);
			for(var i=0;i<data.length;i++) {
				handleData(data[i]);
			}
			lastTime = data[0].ctime;
		}
		obtainTimer = setTimeout(obtain,1000);
	},function(){
		obtainTimer = setTimeout(obtain,3000);
	});
}
