var popoverState={};
function contextMenuBuilder(id,type) {
	var result;
	if(id && (this.getNodeType(id) == "rubber_floor")) {
		var items = {
					"show-wlkc": {name: "物料库存",disabled:false},
                    "show-ljkc": {name: "零件库存",disabled:false},
                    "show-wlps": {name: "物料配送",disabled:false}
                };
		result = {
			items:items,
			callback:function(key) {
				var _id = id;
				right_wl_area(key);
			}
		};
	} 
	if(id && (this.getNodeType(id) == "zj_area")) {
		var items = {
					"show-zjrw": {name: "质检任务",disabled:false},
                    "show-zjjg": {name: "质检结果",disabled:false},
                    "show-bhgpgl": {name: "不合格品管理",disabled:false}
                };
		result = {
			items:items,
			callback:function(key) {
				var _id = id;
				right_zj_area(key);
			}
		};
	} 
	if(id && (this.getNodeType(id) == "office")) {
		var items = {
					"show-jdgz": {name: "进度跟踪",disabled:false},
                    "show-sscx": {name: "实时查询",disabled:false},
                    "show-sbtz": {name: "设备台帐",disabled:false},
                    "show-tjbb": {name: "统计报表",disabled:false},
                    "show-ycyj": {name: "异常预警",disabled:false}//,
                    //"show-jygl": {name: "经营管理",disabled:false},
                    //"show-cbgl": {name: "成本管理",disabled:false}
                };
		result = {
			items:items,
			callback:function(key) {
				var _id = id;
				right_office(key);
			}
		};
	} 

	if(id && (this.getNodeType(id) == "jh_area")) {
		var items = {
					"show-jgrw": {name: "加工任务",disabled:false},
                    "show-scjh": {name: "生产计划",disabled:false},
                    "show-jhdd": {name: "计划调度",disabled:false},
                    "show-gddy": {name: "工单打印",disabled:false}
                };
		result = {
			items:items,
			callback:function(key) {
				var _id = id;
				right_jh_area(key);
			}
		};
	} 

	if(id && (this.getNodeType(id) == "gy_area")) {
		var items = {
					"show-ljgl": {name: "零件管理",disabled:false},
                    "show-gygl": {name: "工艺管理",disabled:false},
                    "show-cxgl": {name: "程序管理",disabled:false}
                };
		result = {
			items:items,
			callback:function(key) {
				var _id = id;
				right_gy_area(key);
			}
		};
	} 

	if(id && (this.getNodeType(id) == "xx_area")) {
		var items = {
					"show-sbwh": {name: "设备维护",disabled:false},
                    "show-nlzgl": {name: "能力组管理",disabled:false}
                };
		result = {
			items:items,
			callback:function(key) {
				var _id = id;
				right_xx_area(key);
			}
		};
	} 

	return result;			 
}
function handleItemDbclick(id,type) {
	if(this.getNodeType(id) == 'cement_floor') {
		var url = ui.getUrl("/mon/ssjk/query_ssjk");
		window.open(url);
	}
}
