function right_wl_area(key){
	switch(key) {
		case "show-wlkc" :
			var url = ui.getUrl("/wm/kcgl/query_kcgl");
			window.open(url);
			break;
		case "show-ljkc" :
			var url = ui.getUrl("/wm/ljkcglrk/query_ljkcgl");
			window.open(url);
			break;
		case "show-wlps" :
			var url = ui.getUrl("/mf/wlps/query_wlps");
			window.open(url);
			break;

		default:ui.info("click context menu : "+key);
	}
}

function right_zj_area(key){
	switch(key) {
		case "show-zjrw" :
			ui.alert("质检任务");
			break;
		case "show-zjjg" :
			ui.alert("质检结果");
			break;
		case "show-bhgpgl" :
			ui.alert("不合格品管理");
			break;
		default:ui.info("click context menu : "+key);
	}
}

function right_office(key){
	switch(key) {
		case "show-jdgz" :
