			window.open(url);
			break;
		case "show-sscx" :
			var url = ui.getUrl("/que/scxxsscx/scxxsscxIndex");
			window.open(url);
			break;
		case "show-sbtz" :
			var url = ui.getUrl("/em/sbtz/query_sbtz");
			window.open(url);
			break;
		case "show-tjbb" :
			var url = ui.getUrl("/rep/tjbb/show_tjbb");
			window.open(url);
			break;
		case "show-ycyj" :
			var url = ui.getUrl("/war/ycgj/query_ycgj");
